     *  @notice Used by Router and CygnusFactory to deploy shuttles
     */
    bytes32 internal constant COLLATERAL_INIT_CODE_HASH =
        0x3dbc8390e0d0bece595895bdf8c0fdac5716d588a2dbc8041831d04c7d965422;

