contracts/cygnus-periphery/BorrowableHar.content:
// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

// Dependencies
import {ICygnusHarvester} from "./interfaces/ICygnusHarvester.sol";
import {ReentrancyGuard} from "./utils/ReentrancyGuard.sol";

// Libraries
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {FixedPointMathLib} from "./libraries/FixedPointMathLib.sol";

// Interfaces
import {IERC20} from "./interfaces/core/IERC20.sol";
import {IHangar18} from "./interfaces/core/IHangar18.sol";
import {ICygnusBorrow} from "./interfaces/core/ICygnusBorrow.sol";
import {IAggregationRouterV5, IAggregationExecutor} from "./interfaces/core/IAggregationRouterV5.sol";

/**
 *  @title  BorrowableHarvester Contract that harvests rewards from a CygnusBorrow contract and reinvests it to
 *          add it to the totalBalance. It uses 1inch aggregator on-chain to swap the rewards to the underlying
 *          borrowable asset (USDC)
 *  @author CygnusDAO
 *  @notice The harvester should only be called from the CygnusBorrow contract via the `reinvestRewards` function.
 *          The function should harvest the rewards and send to this contract the rewards + amounts + swapData.
 *          In order to build the 1inch swap data, the caller can call `getRewards()` via a static call in the
 *          borrowable contract to receive an array of tokens and an array of amounts harvested.
 *  @notice We send USDC to the borrowable contract instead of this address, as opposed to collateral strategies,
 *          saving us a transfer call. See destReceiver in 1inch function.
 *
 *                         ┌───────────────┐       ┌─────────────┐            ┌─────────────┐
 *                         │ 1. Borrowable │       │ 2. Rewarder │            │  4. 1Inch   │
 *                         └───────────────┘       └─────────────┘            └─────────────┘
 *                                 |                      |                          |
 *                                 |    Deposit USDC      |                          |
 *                                 |--------------------->|                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |    Accrue Rewards    |                          |
 *                                 |<---------------------+                          |
 *                                 |                                                 |
 *                                 |                                                 |
 *                                 |              ┌───────────────┐                  |
 *              reinvestRewards()  |------------->│  3. Harvester │                  |
 *                                 |              └───────────────┘                  |
 *                                 |                      |                          |
 *                                 |                      |   Swap Rewards Tokens    |
 *                                 |                      |------------------------->|
 *                                 |                                                 |
 *                                 |              Send USDC to borrowable            |
 *                                 |<------------------------------------------------|
 *
 */
contract BorrowableHarvester is ICygnusHarvester, ReentrancyGuard {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          1. LIBRARIES
      ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib For safe transfers of Erc20 tokens
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @custom:struct Harvester Struct for the strategy
     *  @custom:member underlying Address of the borrowable's underlying (stablecoin)
     *  @custom:member wantToken Address of the optimal token for swaps. Harvester can only swap to this token, else reverts.
     */
    struct Harvester {
        address underlying;
        address wantToken;
    }

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /// @notice Total collaterals
    mapping(address => Harvester) public getHarvester;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    IAggregationRouterV5 public constant override ONE_INCH_ROUTER_V5 =
        IAggregationRouterV5(0x1111111254EEB25477B68fb85Ed929f73A960582);

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address[] public override allHarvesters;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address[] public override allX1RewardTokens;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public constant override MIN_X1_REWARD = 0;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public constant override MAX_X1_REWARD = 1e18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    string public constant override name = "Cygnus Harvester: Sonne Rewards";

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public override x1VaultReward = 0.20e18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    IHangar18 public immutable override hangar18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address public immutable override nativeToken;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address public immutable override cygnusX1Vault;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public override lastX1Collect;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Constructs the harvester, use the hangar18 contract to get important addresses
     *  @param _hangar18 The address of the contract that deploys Cygnus lending pools on this chain
     */
    constructor(IHangar18 _hangar18) {
        // Hangar18 on this chain
        hangar18 = _hangar18;

        // Get native token for this chain (ie WETH)
        nativeToken = _hangar18.nativeToken();

        // Vault
        cygnusX1Vault = _hangar18.cygnusX1Vault();
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            4. MODIFIERS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:modifier cygnusAdmin Controls important parameters in both Collateral and Borrow contracts 👽
     */
    modifier cygnusAdmin() {
        checkAdmin();
        _;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Internal check for msg.sender admin, checks factory's current admin 👽
     */
    function checkAdmin() private view {
        // Current admin from the factory
        address admin = hangar18.admin();

        /// @custom:error MsgSenderNotAdmin Avoid unless caller is Cygnus Admin
        if (msg.sender != admin) {
            revert CygnusHarvester__MsgSenderNotAdmin({sender: msg.sender, admin: admin});
        }
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Adds a reward token to be collected by the X1 Vault on this chain
     *  @param rewardToken The address of the token to be added
     */
    function addRewardTokenPrivate(address rewardToken) private {
        // Load array to memory, gas savings
        address[] memory tokens = allX1RewardTokens;

        // Loop through all reward tokens
        for (uint256 i = 0; i < tokens.length; i++) {
            // If reward token is already included return and exit
            if (rewardToken == tokens[i]) return;
        }

        // Push reward token to array
        allX1RewardTokens.push(rewardToken);

        /// @custom:event NewX1RewardToken
        emit NewX1RewardToken(rewardToken, allX1RewardTokens.length);
    }

    /**
     *  @notice Uses the 1inch Aggregator. It updates the amount to swap in case the estimations are slightly off
     *  @dev Swaps tokens using the 1inch aggregator with the provided swap data and updated amount.
     *  @dev Requirements:
     *       - The `srcToken` must be the rewards token.
     *       - The `dstToken` must be the underlying token.
     *       - The `dstReceiver` must be an initialized borrowable
     *  @param swapData Encoded data that specifies the swap details for the 1inch aggregator
     *  @param token The token we should be swapping
     *  @param amount The updated amount of tokens to be swapped.
     *  @return amountOut The amount of `dstToken` received after the swap.
     */
    function swapTokensPrivateInch(
        bytes calldata swapData,
        address token,
        address wantToken,
        uint256 amount,
        address receiver
    ) private returns (uint256 amountOut) {
        // Get aggregation executor, swap params and the encoded calls for the executor from 1inch API call
        // prettier-ignore
        (address caller, IAggregationRouterV5.SwapDescription memory desc, /* permit */, bytes memory data) = abi
            .decode(swapData, (address, IAggregationRouterV5.SwapDescription, bytes, bytes));

        // Update swap amount to current balance of src token (if needed)
        if (desc.amount != amount) desc.amount = amount;

        /// @custom:error SrcTokenNotValid Avoid swapping anything but rewards token
        if (address(desc.srcToken) != token) {
            revert CygnusHarvester__SrcTokenNotValid({srcToken: address(desc.srcToken), token: token});
        }

        /// @custom:error DstTokenNotValid Avoid swapping to anything but the want token
        if (address(desc.dstToken) != wantToken) {
            revert CygnusHarvester__DstTokenNotValid({dstToken: address(desc.dstToken), token: wantToken});
        }

        // NOTE: We optimistically send the swap amount to an initialized borrowable. It's odd but saves us from having to do
        //       a transfer

        /// @custom:error DstReceiverNotValid Avoid swapping to another address except borrowable
        if (desc.dstReceiver != receiver) {
            revert CygnusHarvester__DstReceiverNotValid({dstReceiver: desc.dstReceiver, receiver: msg.sender}); //
        }

        // Allow 1inch router to access our `srcToken` (REWARDS_TOKEN)
        // This is safe because we only approve a constant (1inch router) and we only use `swap()` from the router which
        // transfers from sender
        approveTokenPrivate(address(desc.srcToken), address(ONE_INCH_ROUTER_V5), desc.amount);

        // Swap `srcToken` to `dstToken` with no permit
        (amountOut, ) = ONE_INCH_ROUTER_V5.swap(IAggregationExecutor(caller), desc, new bytes(0), data);
    }

    /**
     *  @notice Sets `amount` of ERC20 `token` for `to` to manage on behalf of the current contract
     *  @param token The address of the token we are approving
     *  @param amount The amount to approve
     */
    function approveTokenPrivate(address token, address to, uint256 amount) private {
        // Check allowance for `router` for deposit
        if (IERC20(token).allowance(address(this), to) >= amount) {
            return;
        }

        // Is less than amount, safe approve max
        token.safeApprove(to, type(uint256).max);
    }

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function harvestersLength() public view override returns (uint256) {
        // Return the amount of initialized harvesters
        return allHarvesters.length;
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function x1RewardTokensLength() public view override returns (uint256) {
        // Return the amount of tokens we are sending to X1 Vault
        return allX1RewardTokens.length;
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function rewardTokenBalance(address rewardToken) public view override returns (uint256) {
        // Get the balance of `rewardToken` which is the left-over after harvests
        return rewardToken.balanceOf(address(this));
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function rewardTokenBalanceAtIndex(uint256 index) public view override returns (uint256) {
        // Get our balance of reward token at `index`
        return allX1RewardTokens[index].balanceOf(address(this));
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @dev Harvest rewards and add liquidity to the pool
     *  @param swapData The 1inch swap data for each token
     *  @return liquidity The amount of LP tokens received from adding liquidity
     */
    function reinvestRewards(address borrowable, bytes[] calldata swapData) external override returns (uint256 liquidity) {
        // ─────────────────────── 1. Get harvester
        // Harvester for borrowable
        Harvester memory harvester = getHarvester[borrowable];

        /// @custom:error HarvesterNotInitialized
        if (harvester.underlying == address(0)) {
            revert CygnusHarvester__HarvesterNotInitialized({harvester: msg.sender});
        }

        // Harvest rewards
        (address[] memory tokens, uint256[] memory amounts) = ICygnusBorrow(borrowable).getRewards();

        // ─────────────────────── 2. Harvest and convert rewards to optimal token

        // Loop through each token reward
        for (uint256 i = 0; i < tokens.length; ) {
            // If token is not want and we have rewards swap
            if (amounts[i] > 0) {
                // ─────────────── 3. Swap reward to `wantToken`
                // Transfer token from Collateral
                tokens[i].safeTransferFrom(msg.sender, address(this), amounts[i]);

                // Check that token is not wantToken
                if (tokens[i] != harvester.wantToken) {
                    // Pass swap data `i` along with the token to be swapped
                    liquidity += swapTokensPrivateInch(
                        // The swap data for token `i` from harvested rewards
                        swapData[i],
                        // The token being swapped
                        tokens[i],
                        // The token we should be receiving.
                        // Passing any other token in the 1inch call will cause tx to revert
                        harvester.wantToken,
                        // The amount of token `i` we are swapping to wantToken and
                        // reinvesting for the borrowable pools
                        amounts[i],
                        // Receiver
                        borrowable
                    );
                }
                // token is wantToken, add to liquidity
                else liquidity += amounts[i];
            }
            unchecked {
                // Next iteration
                i++;
            }
        }

        /// @custom;error CantReinvestZero
        if (liquidity == 0) revert CygnusHarvester__CantReinvestZero();

        // Never underflows
        unchecked {
            // Reduce liquidity by harvestFee percentage
            liquidity -= liquidity.mulWad(x1VaultReward);
        }

        // Reinvest
        ICygnusBorrow(borrowable).reinvestRewards_y7b(liquidity);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security non-reentrant
     */
    function collectX1RewardsAll() external override nonReentrant {
        // Gas savings, get all reward tokens
        address[] memory rewardTokens = allX1RewardTokens;

        // Start at 0
        uint256 totalTokens;

        // Loop through each reward token
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            // Get our balance of reward token `i`
            uint256 balance = rewardTokens[i].balanceOf(address(this));

            if (balance > 0) {
                // Transfer token balance to X1 Vault
                rewardTokens[i].safeTransfer(cygnusX1Vault, balance);

                // Add to total tokens transfered to vault
                unchecked {
                    totalTokens++;
                }
            }
        }

        /// @custom:event CygnusX1VaultCollect
        emit CygnusX1VaultCollect(lastX1Collect = block.timestamp, msg.sender, totalTokens);
    }

    //  Admin 👽  //

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function initializeHarvester(address borrowable) external override cygnusAdmin {
        // Get harvester from msg.sender
        Harvester memory harvester = getHarvester[borrowable];

        /// @custom:error HarvesterAlreadyInitialized
        if (harvester.underlying != address(0)) {
            revert CygnusHarvester__HarvesterAlreadyInitialized({harvester: borrowable});
        }

        // Get the stablecoin underlying for this borrowable
        address usd = ICygnusBorrow(borrowable).underlying();

        // Set harvester
        getHarvester[borrowable] = Harvester({underlying: usd, wantToken: usd});

        // Check and add tokenA to reward token list
        addRewardTokenPrivate(usd);

        // Add to array
        allHarvesters.push(borrowable);

        /// @custom:event InitializeHarvester
        emit InitializeHarvester(allHarvesters.length, borrowable, usd);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function newX1VaultReward(uint256 vaultReward) external override cygnusAdmin {
        /// @custom:error VaultRewardNotInRange
        if (vaultReward > MAX_X1_REWARD || vaultReward < MIN_X1_REWARD) {
            revert CygnusHarvester__X1RewardNotInRange({min: MIN_X1_REWARD, max: MAX_X1_REWARD, reward: vaultReward});
        }

        // Old percentage which was sent to the X1 Vault from each harvest
        uint256 oldX1VaultReward = vaultReward;

        // Store new percentage
        x1VaultReward = vaultReward;

        /// @custom:event NewX1VaultReward
        emit NewX1VaultReward(msg.sender, oldX1VaultReward, vaultReward);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function collectX1RewardToken(address rewardToken) external override cygnusAdmin {
        // Get our balance of reward token `i`
        uint256 balance = rewardToken.balanceOf(address(this));

        if (balance > 0) {
            // Transfer token balance to X1 Vault
            rewardToken.safeTransfer(cygnusX1Vault, balance);
        }

        /// @custom:event CygnusX1VaultCollect
        emit CygnusX1VaultCollect(lastX1Collect = block.timestamp, msg.sender, 1);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function addX1VaultRewardToken(address rewardToken) external override cygnusAdmin {
        // Add reward private
        // Checks the array and if token does not exist then we add and emit an event,
        // else it returns and escapes function. TX always succeeds.
        addRewardTokenPrivate(rewardToken);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function sweepToken(address token) external override cygnusAdmin {
        // Get token balance
        uint256 balance = token.balanceOf(address(this));

        // If positive balance then transfer
        if (balance > 0) token.safeTransfer(msg.sender, balance);
    }
}

contracts/cygnus-periphery/CollateralHar.content:
// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

// Dependencies
import { ICygnusHarvester } from "./interfaces/ICygnusHarvester.sol";
import { ReentrancyGuard } from "./utils/ReentrancyGuard.sol";

// Libraries
import { CygnusDexLib } from "./libraries/CygnusDexLib.sol";
import { SafeTransferLib } from "./libraries/SafeTransferLib.sol";
import { FixedPointMathLib } from "./libraries/FixedPointMathLib.sol";

// Interfaces
import { IERC20 } from "./interfaces/core/IERC20.sol";
import { IHangar18 } from "./interfaces/core/IHangar18.sol";
import { ICygnusCollateral } from "./interfaces/core/ICygnusCollateral.sol";
import { IAggregationRouterV5, IAggregationExecutor } from "./interfaces/core/IAggregationRouterV5.sol";

// Strategy
import { IDexPair } from "./interfaces/core/CollateralVoid/IDexPair.sol";
import { IDexRouter } from "./interfaces/core/CollateralVoid/IDexRouter.sol";

/**
 *  @title  CollateralHarvester Contract that harvests rewards from a CygnusCollateral contract and reinvests it to
 *          add it to the totalBalance. It uses 1inch aggregator on-chain to swap the rewards to the underlying
 *          borrowable asset (LP Token)
 *  @author CygnusDAO
 *  @notice The harvester should only be called from the CygnusCollateral contract via the `reinvestRewards` function.
 *          The function should harvest the rewards and send to this contract the rewards + amounts + swapData.
 *          In order to build the 1inch swap data, the caller can call `getRewards()` via a static call in the
 *          collateral contract to receive an array of tokens and an array of amounts harvested.
 *
 *                         ┌───────────────┐       ┌─────────────┐            ┌─────────────┐
 *                         │ 1. Collateral │       │ 2. Rewarder │            │  4. 1Inch   │
 *                         └───────────────┘       └─────────────┘            └─────────────┘
 *                                 |                      |                          |
 *                                 |   Deposit LP Tokens  |                          |
 *                                 |--------------------->|                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |                      |                          |
 *                                 |    Accrue Rewards    |                          |
 *                                 |<---------------------+                          |
 *                                 |                                                 |
 *                                 |                                                 |
 *                                 |              ┌───────────────┐                  |
 *              reinvestRewards()  |------------->│  3. Harvester │                  |
 *                                 |              └───────────────┘                  |
 *                                 |                      |                          |
 *                                 |                      |    Swap Rewards Tokens   |
 *                                 |                      |------------------------->|
 *                                 |                      |                          |
 *                                 |                      |  Receive Token0/Token1   |
 *                                 |                      |<-------------------------|
 *                                 |                      |
 *                                 |    Add Liquidity     |
 *                                 |    to Collateral     |
 *                                 |<---------------------+
 *
 */
contract CollateralHarvester is ICygnusHarvester, ReentrancyGuard {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          1. LIBRARIES
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib For safe transfers of Erc20 tokens
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @dev A struct representing a harvester that holds information about the LP token pair and its components
     *  @custom:member underlying The address of the LP token pair
     *  @custom:member wantToken The address of the token the harvester wants to swap rewards into (tokenA)
     *  @custom:member needToken The address of the token that the harvester needs to provide as liquidity (tokenB)
     */
    struct Harvester {
        address underlying;
        address wantToken;
        address needToken;
    }

    /**
     *  @notice VELO The address of the base rewards token
     */
    address private constant VELO = 0x3c8B650257cFb5f272f799F5e2b4e65093a11a05;

    /**
     *  @notice The address of the Velo Router to add liquidity to
     */
    IDexRouter private constant DEX_ROUTER = IDexRouter(0x9c12939390052919aF3155f41Bf4160Fd3666A6f);

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /// @notice Total collaterals
    mapping(address => Harvester) public getHarvester;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    IAggregationRouterV5 public constant override ONE_INCH_ROUTER_V5 =
        IAggregationRouterV5(0x1111111254EEB25477B68fb85Ed929f73A960582);

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address[] public override allHarvesters;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address[] public override allX1RewardTokens;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    string public constant override name = "Cygnus Harvester: VELO Rewards";

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public constant override MIN_X1_REWARD = 0;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public constant override MAX_X1_REWARD = 1e18;

    // Harvester Reward
    uint256 public constant HARVESTER_REWARD = 0.03e18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public override x1VaultReward = 0.20e18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    IHangar18 public immutable override hangar18;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address public immutable override nativeToken;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    address public immutable override cygnusX1Vault;

    /**
     *  @inheritdoc ICygnusHarvester
     */
    uint256 public override lastX1Collect;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Constructs the harvester, use the hangar18 contract to get important addresses
     *  @param _hangar18 The address of the contract that deploys Cygnus lending pools on this chain
     */
    constructor(IHangar18 _hangar18) {
        // Hangar18 on this chain
        hangar18 = _hangar18;

        // Get native token for this chain (ie WETH)
        nativeToken = _hangar18.nativeToken();

        // Vault
        cygnusX1Vault = _hangar18.cygnusX1Vault();

        // Add DEX token
        addRewardTokenPrivate(VELO);
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            4. MODIFIERS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:modifier cygnusAdmin Controls important parameters in both Collateral and Borrow contracts 👽
     */
    modifier cygnusAdmin() {
        checkAdmin();
        _;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Internal check for msg.sender admin, checks factory's current admin 👽
     */
    function checkAdmin() private view {
        // Current admin from the factory
        address admin = hangar18.admin();

        /// @custom:error MsgSenderNotAdmin Avoid unless caller is Cygnus Admin
        if (msg.sender != admin) {
            revert CygnusHarvester__MsgSenderNotAdmin({ sender: msg.sender, admin: admin });
        }
    }

    /**
     *  @notice Calculates the optimal order of the tokens in a given LP token pair for trading.
     *  @dev This function gets the address of the underlying LP token pair and sorts the tokens based on their reserve value.
     *  @param lpTokenPair Address of the LP token pair.
     *  @return tokenA Address of the optimal token
     *  @return tokenB Address of the need token
     */
    function getOptimalDstToken(address lpTokenPair) private view returns (address tokenA, address tokenB) {
        // Token0 from the LP
        address token0 = IDexPair(lpTokenPair).token0();

        // Token1 from the LP
        address token1 = IDexPair(lpTokenPair).token1();

        // Check if rewards token is already token0 or token1 from LP
        if (token0 == VELO || token1 == VELO) {
            // Check which token is rewardsToken
            (tokenA, tokenB) = token0 == VELO ? (token0, token1) : (token1, token0);
        } else {
            // Check if token0 or token1 is native token
            if (token0 == nativeToken || token1 == nativeToken) {
                // Check which token is nativeToken
                (tokenA, tokenB) = token0 == nativeToken ? (token0, token1) : (token1, token0);
            } else {
                // Assign tokenA and tokenB to token0 and token1 respectively
                (tokenA, tokenB) = (token0, token1);
            }
        }
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Adds a reward token to be collected by the X1 Vault on this chain
     *  @param rewardToken The address of the token to be added
     */
    function addRewardTokenPrivate(address rewardToken) private {
        // Load array to memory, gas savings
        address[] memory tokens = allX1RewardTokens;

        // Loop through all reward tokens
        for (uint256 i = 0; i < tokens.length; i++) {
            // If reward token is already included return and exit
            if (rewardToken == tokens[i]) return;
        }

        // Push reward token to array
        allX1RewardTokens.push(rewardToken);

        /// @custom:event NewX1RewardToken
        emit NewX1RewardToken(rewardToken, allX1RewardTokens.length);
    }

    /**
     *  @notice Uses the 1inch Aggregator. It updates the amount to swap in case the estimations are slightly off
     *  @dev Swaps tokens using the 1inch aggregator with the provided swap data and updated amount.
     *  @dev Requirements:
     *       - The `srcToken` must be the rewards token.
     *       - The `dstToken` must be the underlying token.
     *       - The `dstReceiver` must be this contract address.
     *  @param swapData Encoded data that specifies the swap details for the 1inch aggregator
     *  @param token The token we should be swapping
     *  @param amount The updated amount of tokens to be swapped.
     *  @return amountOut The amount of `dstToken` received after the swap.
     */
    function swapTokensPrivateInch(
        bytes calldata swapData,
        address token,
        address wantToken,
        uint256 amount
    ) private returns (uint256 amountOut) {
        // Get aggregation executor, swap params and the encoded calls for the executor from 1inch API call
        // prettier-ignore
        (address caller, IAggregationRouterV5.SwapDescription memory desc, bytes memory permit, bytes memory data) = abi
            .decode(swapData, (address, IAggregationRouterV5.SwapDescription, bytes, bytes));

        // Update swap amount to current balance of src token (if needed)
        if (desc.amount != amount) desc.amount = amount;

        /// @custom:error SrcTokenNotValid Avoid swapping anything but rewards token
        if (address(desc.srcToken) != token) {
            revert CygnusHarvester__SrcTokenNotValid({ srcToken: address(desc.srcToken), token: token });
        }

        /// @custom:error DstTokenNotValid Avoid swapping to anything but the want token
        if (address(desc.dstToken) != wantToken) {
            revert CygnusHarvester__DstTokenNotValid({ dstToken: address(desc.dstToken), token: wantToken });
        }

        /// @custom:error DstReceiverNotValid Avoid swapping to another address
        if (desc.dstReceiver != address(this)) {
            revert CygnusHarvester__DstReceiverNotValid({ dstReceiver: desc.dstReceiver, receiver: address(this) });
        }

        // Allow 1inch router to access our `srcToken` (REWARDS_TOKEN)
        // This is safe because we only approve a constant (1inch router) and we only use `swap()` from the router which
        // transfers from sender
        approveTokenPrivate(address(desc.srcToken), address(ONE_INCH_ROUTER_V5), desc.amount);

        // Swap `srcToken` to `dstToken` with no permit
        (amountOut, ) = ONE_INCH_ROUTER_V5.swap(IAggregationExecutor(caller), desc, permit, data);
    }

    /**
     *  @notice Sets `amount` of ERC20 `token` for `to` to manage on behalf of the current contract
     *  @param token The address of the token we are approving
     *  @param amount The amount to approve
     */
    function approveTokenPrivate(address token, address to, uint256 amount) private {
        // Check allowance for `router` for deposit
        if (IERC20(token).allowance(address(this), to) >= amount) {
            return;
        }

        // Is less than amount, safe approve max
        token.safeApprove(to, type(uint256).max);
    }

    /**
     *  @notice Swap tokens using the dex router for the second swap
     *  @param tokenIn The address of the token we are swapping
     *  @param tokenOut The address of the token we are receiving
     *  @param amount Amount of `tokenIn` we are swapping to `tokenOut`
     */
    function swapTokensPrivateDex(address tokenIn, address tokenOut, uint256 amount) private returns (uint256) {
        // Check approve tokenIn
        approveTokenPrivate(tokenIn, address(DEX_ROUTER), amount);

        // Swap tokenIn to tokenOut
        uint256[] memory amounts = DEX_ROUTER.swapExactTokensForTokensSimple(
            amount,
            0,
            tokenIn,
            tokenOut,
            false,
            address(this),
            block.timestamp
        );

        // Return amount out of tokenOut (last index)
        return amounts[amounts.length - 1];
    }

    /**
     * @dev Adds liquidity to the designated DEX pool.
     * @param tokenA The address of the first token to add liquidity for.
     * @param tokenB The address of the second token to add liquidity for.
     * @param amountA The amount of the first token to add to the liquidity pool.
     * @param amountB The amount of the second token to add to the liquidity pool.
     * @return liquidity The amount of liquidity tokens received
     */
    function addLiquidityPrivate(
        address tokenA,
        address tokenB,
        uint256 amountA,
        uint256 amountB,
        address cygnus
    ) private returns (uint256 liquidity) {
        // Check tokenA approval
        approveTokenPrivate(tokenA, address(DEX_ROUTER), amountA);

        // Check tokenB approval
        approveTokenPrivate(tokenB, address(DEX_ROUTER), amountB);

        // Never underflows
        unchecked {
            // Reduce amountA by harvestFee percentage
            amountA -= amountA.mulWad(x1VaultReward);

            // Reduce amountB by harvestFee percentage
            amountB -= amountB.mulWad(x1VaultReward);
        }

        // Mint LP directly to msg.sender which must be an initialized collateral, else transaction would have
        // reverted by now.
        (, , liquidity) = DEX_ROUTER.addLiquidity(
            tokenA,
            tokenB,
            false,
            amountA,
            amountB,
            0,
            0,
            cygnus,
            block.timestamp
        );
    }

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function harvestersLength() public view override returns (uint256) {
        // Return the amount of initialized harvesters
        return allHarvesters.length;
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function x1RewardTokensLength() public view override returns (uint256) {
        // Return the amount of tokens we are sending to X1 Vault
        return allX1RewardTokens.length;
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function rewardTokenBalance(address rewardToken) public view override returns (uint256) {
        // Get the balance of `rewardToken` which is the left-over after harvests
        return rewardToken.balanceOf(address(this));
    }

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function rewardTokenBalanceAtIndex(uint256 index) public view override returns (uint256) {
        // Get our balance of reward token at `index`
        return allX1RewardTokens[index].balanceOf(address(this));
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusHarvester
     */
    function reinvestRewards(
        address collateral,
        bytes[] calldata swapData
    ) external override returns (uint256 liquidity) {
        // ─────────────────────── 1. Get harvester
        // Harvester for collateral
        Harvester storage harvester = getHarvester[collateral];

        /// @custom:error HarvesterNotInitialized
        if (harvester.underlying == address(0)) {
            revert CygnusHarvester__HarvesterNotInitialized({ harvester: msg.sender });
        }

        // ─────────────────────── 2. Harvest and convert rewards to optimal token
        // Harvest rewards
        (address[] memory tokens, uint256[] memory amounts) = ICygnusCollateral(collateral).getRewards();

        // Get want token
        address wantToken = harvester.wantToken;

        // Amount of want token
        uint256 wantAmount;

        // Loop through each token reward
        for (uint256 i = 0; i < tokens.length; ) {
            // If token is not want and we have rewards swap
            if (amounts[i] > 0) {
                // ─────────────── 3. Swap reward to `wantToken`
                // Transfer token from Collateral
                tokens[i].safeTransferFrom(collateral, address(this), amounts[i]);

                // Check that token is not wantToken
                if (tokens[i] != wantToken) {
                    // Pass swap data `i` along with the token to be swapped
                    wantAmount += swapTokensPrivateInch(
                        // The swap data for token `i` from harvested rewards
                        swapData[i],
                        // The token being swapped
                        tokens[i],
                        // The token we should be receiving.
                        // Passing any other token in the 1inch call will cause tx to revert
                        wantToken,
                        // The amount of token `i` we are swapping to wantToken and
                        // reinvesting for the borrowable pools
                        amounts[i]
                    );
                }
                // token is wantToken, add to `wantAmount`
                else wantAmount += amounts[i];
            }
            unchecked {
                // Next iteration
                i++;
            }
        }

        /// @custom;error CantReinvestZero
        if (wantAmount == 0) revert CygnusHarvester__CantReinvestZero();

        // ─────────────────────── 4. Convert wantToken to LP Token underlying
        // Reserves of wantToken
        uint256 reservesA = IERC20(wantToken).balanceOf(harvester.underlying);

        // Get optimal swap amount of wantToken (use maximum swap fee for velo which is 0.05%, ie 9995/1000)
        uint256 swapAmount = CygnusDexLib.optimalDepositA(wantAmount, reservesA, 5);

        // Swap optimal amount to wantToken to needToken for liquidity deposit
        uint256 needTokenAmount = swapTokensPrivateDex(wantToken, harvester.needToken, swapAmount);

        // Add liquidity and get LP Token
        liquidity = addLiquidityPrivate(
            wantToken,
            harvester.needToken,
            wantAmount - swapAmount,
            needTokenAmount,
            collateral
        );

        // Reinvest
        ICygnusCollateral(collateral).reinvestRewards_y7b(liquidity);
    }

    function harvestToVault(address collateral) external nonReentrant {
        /// @custom:error X1VaultRewardNotOne
        if (x1VaultReward != 1e18) revert CygnusHarvester__X1VaultRewardNotOne();

        // Harvest rewards
        (address[] memory tokens, uint256[] memory amounts) = ICygnusCollateral(collateral).getRewards();

        // Loop through each token
        for (uint256 i = 0; i < tokens.length; i++) {
            // If amount `i` is greater than 0
            if (amounts[i] > 0) {
                // Caller reward
                uint256 callerReward = amounts[i].mulWad(HARVESTER_REWARD);

                // Transfer to harvester
                tokens[i].safeTransferFrom(collateral, msg.sender, callerReward);

                // Transfer the rest to the vault
                tokens[i].safeTransferFrom(collateral, cygnusX1Vault, amounts[i] - callerReward);
            }
        }
    }

    //  Admin 👽  //

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function initializeHarvester(address collateral) external override cygnusAdmin {
        // Get harvester from msg.sender
        Harvester memory harvester = getHarvester[collateral];

        /// @custom:error HarvesterAlreadyInitialized
        if (harvester.underlying != address(0)) {
            revert CygnusHarvester__HarvesterAlreadyInitialized({ harvester: collateral });
        }

        // Get the LP Token pair for this collateral
        address lpTokenPair = ICygnusCollateral(collateral).underlying();

        // Get optimal wantToken to swap rewards to and cache the other token of the LP
        (address wantToken, address needToken) = getOptimalDstToken(lpTokenPair);

        // Set harvester
        getHarvester[collateral] = Harvester({ underlying: lpTokenPair, wantToken: wantToken, needToken: needToken });

        // Check and add tokenA to reward token list
        addRewardTokenPrivate(wantToken);

        // Check and add tokenB to reward token list
        addRewardTokenPrivate(needToken);

        // Add to array
        allHarvesters.push(collateral);

        /// @custom:event InitializeHarvester
        emit InitializeHarvester(allHarvesters.length, collateral, lpTokenPair);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function newX1VaultReward(uint256 vaultReward) external override cygnusAdmin {
        /// @custom:error VaultRewardNotInRange
        if (vaultReward > MAX_X1_REWARD || vaultReward < MIN_X1_REWARD) {
            revert CygnusHarvester__X1RewardNotInRange({ min: MIN_X1_REWARD, max: MAX_X1_REWARD, reward: vaultReward });
        }

        // Old percentage which was sent to the X1 Vault from each harvest
        uint256 oldX1VaultReward = vaultReward;

        // Store new percentage
        x1VaultReward = vaultReward;

        /// @custom:event NewX1VaultReward
        emit NewX1VaultReward(msg.sender, oldX1VaultReward, vaultReward);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security non-reentrant
     */
    function collectX1RewardsAll() external override nonReentrant {
        // Gas savings, get all reward tokens
        address[] memory rewardTokens = allX1RewardTokens;

        // Start at 0
        uint256 totalTokens;

        // Loop through each reward token
        for (uint256 i = 0; i < rewardTokens.length; ) {
            // Get our balance of reward token `i`
            uint256 balance = rewardTokens[i].balanceOf(address(this));

            if (balance > 0) {
                // Transfer token balance to X1 Vault
                rewardTokens[i].safeTransfer(cygnusX1Vault, balance);

                // Add to total tokens transfered to vault
                unchecked {
                    totalTokens++;
                }
            }
            unchecked {
                i++;
            }
        }

        /// @custom:event CygnusX1VaultCollect
        emit CygnusX1VaultCollect(lastX1Collect = block.timestamp, msg.sender, totalTokens);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function collectX1RewardToken(address rewardToken) external override cygnusAdmin {
        // Get our balance of reward token `i`
        uint256 balance = rewardToken.balanceOf(address(this));

        if (balance > 0) {
            // Transfer token balance to X1 Vault
            rewardToken.safeTransfer(cygnusX1Vault, balance);
        }

        /// @custom:event CygnusX1VaultCollect
        emit CygnusX1VaultCollect(lastX1Collect = block.timestamp, msg.sender, 1);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function addX1VaultRewardToken(address rewardToken) external override cygnusAdmin {
        // Add reward private
        // Checks the array and if token does not exist then we add and emit an event,
        // else it returns and escapes function. TX always succeeds.
        addRewardTokenPrivate(rewardToken);
    }

    /**
     *  @inheritdoc ICygnusHarvester
     *  @custom:security only-admin 👽
     */
    function sweepToken(address token) external override cygnusAdmin {
        // Get token balance
        uint256 balance = token.balanceOf(address(this));

        // If positive balance then transfer
        if (balance > 0) token.safeTransfer(msg.sender, balance);
    }
}

contracts/cygnus-periphery/CygnusAltair..content:
// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

// Dependencies
import {ICygnusAltair} from "./interfaces/ICygnusAltair.sol";

// Libraries
import {CygnusDexLib} from "./libraries/CygnusDexLib.sol";
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {FixedPointMathLib} from "./libraries/FixedPointMathLib.sol";

// Interfaces
import {IWrappedNative} from "./interfaces/IWrappedNative.sol";
import {IERC20} from "./interfaces/core/IERC20.sol";

// Cygnus Core
import {IHangar18} from "./interfaces/core/IHangar18.sol";
import {ICygnusBorrow} from "./interfaces/core/ICygnusBorrow.sol";
import {ICygnusTerminal} from "./interfaces/core/ICygnusTerminal.sol";
import {ICygnusCollateral} from "./interfaces/core/ICygnusCollateral.sol";

// Permit2
import {IAllowanceTransfer} from "./interfaces/core/IAllowanceTransfer.sol";
import {ISignatureTransfer} from "./interfaces/ISignatureTransfer.sol";

// 1Inch
import {IAggregationRouterV5, IAggregationExecutor} from "./interfaces/core/IAggregationRouterV5.sol";

// Paraswap
import {IAugustusSwapper} from "./interfaces/IAugustusSwapper.sol";

/**
 *  @title  CygnusAltair Periphery contract to interact with Cygnus Core contracts
 *  @author CygnusDAO
 *  @notice The router contract that is used to interact with Cygnus core contracts.
 *
 *          This router is integrated with Paraswap's Augustus Swapper and 1inch's AggregationRouter5 across all
 *          chains, and it works mostly on-chain. The queries are estimated before the first call, following the
 *          same logic for swaps as this contract and then each proceeding call builds on top of the next one.
 *
 *          During the leverage functionality the router borrows USD from the borrowable arm contract, and then
 *          converts it to LP Tokens. What this router does is account for every possible swap scenario between
 *          tokens, using a byte array populated with 1inch data. Before the leverage or de-leverage function call,
 *          we calculate quotes to estimate what the `amount` will be during each swap stage, and we use the data
 *          passed from each step and override the `amount` with the current balance of this contract (both amounts
 *          should be the same, or in some cases could be off by a very small amount).
 *
 *          The max amount of aggswaps that we can perform during a leverage is 1 and de-leverage is 2. Thus the data
 *          passed will always be at most a 2-length byte array.
 *
 *          Functions in this contract allow for:
 *            - Borrowing USD
 *            - Repaying USD
 *            - Liquidating user's with USD (pay back USD, receive CygLP + bonus liquidation reward)
 *            - Flash liquidating a user by selling collateral to the market and receive USD
 *            - Leveraging USD into Liquidity
 *            - Deleveraging Liquidity into USD
 */
abstract contract CygnusAltair is ICygnusAltair {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          1. LIBRARIES
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib For safe transfers of Erc20 tokens
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Internal ───────────────────────────────────────────────  */

    /**
     *  @notice Empty bytes to pass to contracts if needed
     */
    bytes internal constant LOCAL_BYTES = new bytes(0);

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */
    /**
     *  @inheritdoc ICygnusAltair
     */
    string public override name;

    /**
     *  @inheritdoc ICygnusAltair
     */
    address public constant override PERMIT2 = 0x000000000022D473030F116dDEE9F6B43aC78BA3;

    /**
     *  @inheritdoc ICygnusAltair
     */
    address public constant PARASWAP_AUGUSTUS_SWAPPER_V5 = 0xDEF171Fe48CF0115B1d80b88dc8eAB59176FEe57;

    /**
     *  @inheritdoc ICygnusAltair
     */
    address public constant ONE_INCH_ROUTER_V5 = 0x1111111254EEB25477B68fb85Ed929f73A960582;

    /**
     *  @notice Empty permit to deposit leveraged LP amounts
     */
    IAllowanceTransfer.PermitSingle public emptyPermit;

    /**
     *  @inheritdoc ICygnusAltair
     */
    IHangar18 public immutable override hangar18;

    /**
     *  @inheritdoc ICygnusAltair
     */
    address public immutable override usd;

    /**
     *  @inheritdoc ICygnusAltair
     */
    address public immutable override nativeToken;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Constructs the periphery contract. Factory must be deployed on the chain first to get the addresses
     *          of deployers and the wrapped native token (WETH, WFTM, etc.)
     *  @param _hangar18 The address of the Cygnus Factory contract on this chain
     */
    constructor(IHangar18 _hangar18) {
        // Factory
        hangar18 = _hangar18;

        // Assign the native token set at the factory
        nativeToken = _hangar18.nativeToken();

        // Assign the USD address set at the factoryn
        usd = _hangar18.usd();
    }

    /**
     *  @notice Only accept native via fallback from the Wrapped native contract
     */
    receive() external payable {
        /// @custom:error NotNativeTokenSender Only accept from native contract (ie WETH)
        if (msg.sender != nativeToken) {
            revert CygnusAltair__NotNativeTokenSender({poolToken: msg.sender});
        }
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          4. MODIFIERS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:modifier checkDeadline Reverts the transaction if the block.timestamp is after deadline
     */
    modifier checkDeadline(uint256 deadline) {
        _checkDeadline(deadline);
        _;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Internal ───────────────────────────────────────────────  */

    /**
     *  @notice The current block timestamp
     */
    function _checkTimestamp() internal view returns (uint256) {
        return block.timestamp;
    }

    /**
     *  @notice Reverts the transaction if the block.timestamp is after deadline
     *  @param deadline The time by which the transaction must be included to effect the change
     */
    function _checkDeadline(uint256 deadline) internal view {
        /// @custom:error TransactionExpired Avoid transacting past deadline
        if (_checkTimestamp() > deadline) {
            revert CygnusAltair__TransactionExpired({deadline: deadline});
        }
    }

    /**
     *  @notice Checks the `token` balance of this contract
     *  @param token The token to view balance of
     *  @return amount This contract's `token` balance
     */
    function _checkBalance(address token) internal view returns (uint256) {
        // Our balance of `token` (uses solady lib)
        return token.balanceOf(address(this));
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Calls permit function on pool token
     *  @param terminal The address of the collateral or borrowable
     *  @param amount The permit amount
     *  @param deadline Permit deadline
     *  @param permitData Permit data to decode
     */
    function _checkPermit(address terminal, uint256 amount, uint256 deadline, bytes memory permitData) private {
        // Return if no permit data
        if (permitData.length == 0) return;

        // Decode permit data
        (bool approveMax, uint8 v, bytes32 r, bytes32 s) = abi.decode(permitData, (bool, uint8, bytes32, bytes32));

        // Max value
        uint256 value = approveMax ? type(uint256).max : amount;

        // Call permit on terminal token
        ICygnusTerminal(terminal).permit(msg.sender, address(this), value, deadline, v, r, s);
    }

    // Swap tokens via paraswap

    /**
     *  @notice Creates the swap with Paraswap's Augustus Swapper. We don't update the amount, instead we clean dust at the end.
     *          This is because the data is of complex type (Path[] path). We pass the token being swapped and the amount being
     *          swapped to approve the transfer proxy (which is set on augustus wrapped via `getTokenTransferProxy`).
     *  @param swapData The data from Paraswap's `transaction` query
     *  @param srcToken The token being swapped
     *  @param fromAmount The amount of `srcToken` being swapped
     *  @return amountOut The amount received of destination token
     */
    function _swapTokensParaswap(bytes memory swapData, address srcToken, uint256 fromAmount) private returns (uint256 amountOut) {
        // Paraswap's token proxy to approve in srcToken
        address paraswapTransferProxy = IAugustusSwapper(PARASWAP_AUGUSTUS_SWAPPER_V5).getTokenTransferProxy();

        // Approve Paraswap's transfer proxy in `srcToken` if necessary
        _approveToken(srcToken, paraswapTransferProxy, fromAmount);

        // Call the augustus wrapper with the data passed, triggering the fallback function for multi/mega swaps
        (bool success, bytes memory resultData) = PARASWAP_AUGUSTUS_SWAPPER_V5.call{value: msg.value}(swapData);

        /// @custom:error ParaswapTransactionFailed
        if (!success) revert CygnusAltair__ParaswapTransactionFailed();

        // Return amount received - This is off by some very small amount from the actual contract balance.
        // We shouldn't use it directly. Instead, query contract balance of token received
        assembly {
            amountOut := mload(add(resultData, 32))
        }
    }

    // Swap tokens via 1inch

    /**
     *  @notice Creates the swap with 1Inch's AggregatorV5. We pass an extra param `updatedAmount` to eliminate
     *          any slippage from the byte data passed. When calculating the optimal deposit for single sided
     *          liquidity deposit, our calculation can be off for a few mini tokens which don't affect the
     *          data of the aggregation executor, so we pass the tx data as is but update the srcToken amount
     *  @param swapData The data from 1inch `swap` query
     *  @param updatedAmount The balanceOf this contract`s srcToken
     *  @return amountOut The amount received of destination token
     */
    function _swapTokensInch(bytes memory swapData, uint256 updatedAmount) internal returns (uint256 amountOut) {
        // Get aggregation executor, swap params and the encoded calls for the executor from 1inch API call
        (address caller, IAggregationRouterV5.SwapDescription memory desc, bytes memory permit, bytes memory data) = abi.decode(
            swapData,
            (address, IAggregationRouterV5.SwapDescription, bytes, bytes)
        );

        // Update swap amount to current balance of src token (if needed)
        if (desc.amount != updatedAmount) desc.amount = updatedAmount;

        // Approve 1Inch Router in `srcToken` if necessary
        _approveToken(address(desc.srcToken), address(ONE_INCH_ROUTER_V5), desc.amount);

        // Swap `srcToken` to `dstToken` - Aggregator does the necessary minAmount check & we do checks at the end
        // of the leverage/deleverage functions anyways
        (amountOut, ) = IAggregationRouterV5(ONE_INCH_ROUTER_V5).swap(IAggregationExecutor(caller), desc, permit, data);
    }

    /*  ────────────────────────────────────────────── Internal ───────────────────────────────────────────────  */

    /**
     *  @notice Approves permit2 in `token` - This is used to deposit leveraged liquidity back into the CygnusCollateral
     *  @param token The address of the token we are approving the permit2 router in
     *  @param spender The address of the contract we are allowing to move our `token` (CygnusCollateral)
     *  @param amount The amount we are allowing
     */
    function _approvePermit2(address token, address spender, uint256 amount) internal {
        // Get allowance
        (uint160 allowed, , ) = IAllowanceTransfer(PERMIT2).allowance(address(this), token, spender);

        // Return without approving
        if (allowed >= amount) return;

        // We approve to the max uint160 allowed and max allowed deadline
        IAllowanceTransfer(PERMIT2).approve(token, spender, type(uint160).max, type(uint48).max);
    }

    /**
     *  @dev Internal function to swap tokens using the specified aggregator.
     *  @param dexAggregator The aggregator to use for the token swap
     *  @param swapData The encoded swap data for the aggregator.
     *  @param srcToken The source token to swap
     *  @param srcAmount The amount of source token to swap
     *  @return amountOut The amount of swapped tokens received
     */
    function _swapTokensAggregator(
        DexAggregator dexAggregator,
        bytes memory swapData,
        address srcToken,
        uint256 srcAmount
    ) internal returns (uint256 amountOut) {
        // Check which dex aggregator to use
        if (dexAggregator == DexAggregator.PARASWAP) {
            // Swap tokens using ParaSwap aggregator
            amountOut = _swapTokensParaswap(swapData, srcToken, srcAmount);
        } else if (dexAggregator == DexAggregator.ONE_INCH) {
            // Swap tokens using 1inch aggregator
            amountOut = _swapTokensInch(swapData, srcAmount);
        }
    }

    /**
     *  @notice Grants allowance from this contract to a dex' router (or just a contract instead of `router`)
     *  @param token The address of the token we are approving
     *  @param router The address of the dex router we are approving (or just a contract)
     *  @param amount The amount to approve
     */
    function _approveToken(address token, address router, uint256 amount) internal {
        // If allowance is already higher than `amount` return
        if (IERC20(token).allowance(address(this), router) >= amount) return;

        // Approve token
        token.safeApprove(router, type(uint256).max);
    }

    /**
     *  @notice Safe internal function to repay borrowed amount
     *  @param borrowable The address of the Cygnus borrow arm where the borrowed amount was taken from
     *  @param amountMax The max amount that can be repaid
     *  @param borrower The address of the account that is repaying the borrowed amount
     */
    function _maxRepayAmount(address borrowable, uint256 amountMax, address borrower) internal returns (uint256 amount) {
        // Accrue interest first to not leave debt after full repay
        ICygnusBorrow(borrowable).accrueInterest();

        // Get borrow balance of borrower
        // prettier-ignore
        (/* principal */, uint256 borrowedAmount) = ICygnusBorrow(borrowable).getBorrowBalance(borrower);

        // Avoid repaying more than borrowedAmount
        amount = amountMax < borrowedAmount ? amountMax : borrowedAmount;
    }

    /**
     *  @notice Avoid repeating ourselves to make leverage data
     *  @param lpTokenPair The address of the LP Token
     *  @param collateral The address of the collateral of the lending pool
     *  @param borrowable The address of the borrowable of the lending pool
     *  @param lpAmountMin The minimum amount of LP Tokens to receive
     *  @param dexAggregator The dex aggregator to use for the swaps
     *  @param swapData the aggregator swap data to convert USD to liquidity
     */
    function _createLeverageShuttle(
        address lpTokenPair,
        address collateral,
        address borrowable,
        uint256 lpAmountMin,
        DexAggregator dexAggregator,
        bytes[] calldata swapData
    ) private view returns (bytes memory) {
        // Return encoded bytes to pass to borrowbale
        return
            abi.encode(
                AltairLeverageCalldata({
                    lpTokenPair: lpTokenPair,
                    collateral: collateral,
                    borrowable: borrowable,
                    recipient: msg.sender,
                    lpAmountMin: lpAmountMin,
                    dexAggregator: dexAggregator,
                    swapData: swapData
                })
            );
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    //  BORROW ───────────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function borrow(
        address borrowable,
        uint256 amount,
        address recipient,
        uint256 deadline,
        bytes calldata permitData
    ) external virtual override checkDeadline(deadline) {
        // Check permit
        _checkPermit(borrowable, amount, deadline, permitData);

        // Borrow amount
        ICygnusBorrow(borrowable).borrow(msg.sender, recipient, amount, LOCAL_BYTES);
    }

    //  REPAY ────────────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function repay(
        address borrowable,
        uint256 amountMax,
        address borrower,
        uint256 deadline
    ) external virtual override checkDeadline(deadline) returns (uint256 amount) {
        // Ensure that the amount to repay is never more than currently owed.
        // Accrues interest first then gets the borrow balance
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Transfer USD from msg sender to borrow contract
        usd.safeTransferFrom(msg.sender, borrowable, amount);

        // Call borrow to update borrower's borrow balance
        ICygnusBorrow(borrowable).borrow(borrower, address(0), 0, LOCAL_BYTES);
    }

    /**
     *  @inheritdoc ICygnusAltair
     */
    function repayPermit2Allowance(
        address borrowable,
        uint256 amountMax,
        address borrower,
        uint256 deadline,
        IAllowanceTransfer.PermitSingle calldata _permit,
        bytes calldata signature
    ) external virtual override checkDeadline(deadline) returns (uint256 amount) {
        // Ensure that the amount to repay is never more than currently owed.
        // Accrues interest first then gets the borrow balance
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Check for permit (else users can just approve permit2 and skip this by passing an empty
        // PermitSingle and an empty `_signature`)
        if (signature.length > 0) {
            // Set allowance using permit
            IAllowanceTransfer(PERMIT2).permit(
                // The owner of the tokens being approved.
                // We only allow the owner of the tokens to be the depositor, but
                // recipient can be set to another address if owner wants
                msg.sender,
                // Data signed over by the owner specifying the terms of approval
                _permit,
                // The owner's signature over the permit data that was the result
                // of signing the EIP712 hash of `_permit`
                signature
            );
        }

        // Transfer underlying to vault
        IAllowanceTransfer(PERMIT2).transferFrom(msg.sender, borrowable, uint160(amount), usd);

        // Call borrow to update borrower's borrow balance and repay loan
        ICygnusBorrow(borrowable).borrow(borrower, address(0), 0, LOCAL_BYTES);
    }

    /**
     *  @inheritdoc ICygnusAltair
     */
    function repayPermit2Signature(
        address borrowable,
        uint256 amountMax,
        address borrower,
        uint256 deadline,
        ISignatureTransfer.PermitTransferFrom calldata _permit,
        bytes calldata signature
    ) external virtual override checkDeadline(deadline) returns (uint256 amount) {
        // Ensure that the amount to repay is never more than currently owed.
        // Accrues interest first then gets the borrow balance
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Signture transfer
        ISignatureTransfer(PERMIT2).permitTransferFrom(
            // The permit message.
            _permit,
            // The transfer recipient and amount.
            ISignatureTransfer.SignatureTransferDetails({to: borrowable, requestedAmount: amount}),
            // Owner of the tokens and signer of the message.
            msg.sender,
            // The packed signature that was the result of signing
            // the EIP712 hash of `_permit`.
            signature
        );

        // Call borrow to update borrower's borrow balance and repay loan
        ICygnusBorrow(borrowable).borrow(borrower, address(0), 0, LOCAL_BYTES);
    }

    //  LIQUIDATE BORROW ─────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function liquidate(
        address borrowable,
        uint256 amountMax,
        address borrower,
        address recipient,
        uint256 deadline
    ) external virtual override checkDeadline(deadline) returns (uint256 amount, uint256 seizeTokens) {
        // Amount to repay
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Transfer USD
        usd.safeTransferFrom(msg.sender, borrowable, amount);

        // Liquidate
        seizeTokens = ICygnusBorrow(borrowable).liquidate(borrower, recipient, amount, LOCAL_BYTES);
    }

    /**
     *  @inheritdoc ICygnusAltair
     */
    function liquidatePermit2Allowance(
        address borrowable,
        uint256 amountMax,
        address borrower,
        address recipient,
        uint256 deadline,
        IAllowanceTransfer.PermitSingle calldata _permit,
        bytes calldata signature
    ) external virtual override checkDeadline(deadline) returns (uint256 amount, uint256 seizeTokens) {
        // Ensure that the amount to repay is never more than currently owed.
        // Accrues interest first then gets the borrow balance
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Check for permit (else users can just approve permit2 and skip this by passing an empty
        // PermitSingle and an empty `_signature`)
        if (signature.length > 0) {
            // Set allowance using permit
            IAllowanceTransfer(PERMIT2).permit(
                // The owner of the tokens being approved.
                // We only allow the owner of the tokens to be the depositor, but
                // recipient can be set to another address if owner wants
                msg.sender,
                // Data signed over by the owner specifying the terms of approval
                _permit,
                // The owner's signature over the permit data that was the result
                // of signing the EIP712 hash of `_permit`
                signature
            );
        }

        // Transfer underlying to vault
        IAllowanceTransfer(PERMIT2).transferFrom(msg.sender, borrowable, uint160(amount), usd);

        // Liquidate
        seizeTokens = ICygnusBorrow(borrowable).liquidate(borrower, recipient, amount, LOCAL_BYTES);
    }

    /**
     *  @inheritdoc ICygnusAltair
     */
    function liquidatePermit2Signature(
        address borrowable,
        uint256 amountMax,
        address borrower,
        address recipient,
        uint256 deadline,
        ISignatureTransfer.PermitTransferFrom calldata _permit,
        bytes calldata signature
    ) external virtual override checkDeadline(deadline) returns (uint256 amount, uint256 seizeTokens) {
        // Amount to repay
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Signture transfer
        ISignatureTransfer(PERMIT2).permitTransferFrom(
            // The permit message.
            _permit,
            // The transfer recipient and amount.
            ISignatureTransfer.SignatureTransferDetails({to: borrowable, requestedAmount: amount}),
            // Owner of the tokens and signer of the message.
            msg.sender,
            // The packed signature that was the result of signing
            // the EIP712 hash of `_permit`.
            signature
        );

        // Liquidate
        seizeTokens = ICygnusBorrow(borrowable).liquidate(borrower, recipient, amount, LOCAL_BYTES);
    }

    /**
     *  @inheritdoc ICygnusAltair
     */
    function flashLiquidate(
        address borrowable,
        address collateral,
        uint256 amountMax,
        address borrower,
        address recipient,
        uint256 deadline,
        DexAggregator dexAggregator,
        bytes[] calldata swapData
    ) external virtual override checkDeadline(deadline) returns (uint256 amount) {
        // Amount to repay
        amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Get LP TokenPair
        address lpTokenPair = ICygnusCollateral(collateral).underlying();

        // Encode data to bytes
        bytes memory cygnusShuttle = abi.encode(
            AltairLiquidateCalldata({
                lpTokenPair: lpTokenPair,
                collateral: collateral,
                borrowable: borrowable,
                borrower: borrower,
                recipient: recipient,
                repayAmount: amount,
                dexAggregator: dexAggregator,
                swapData: swapData
            })
        );

        // Liquidate
        ICygnusBorrow(borrowable).liquidate(borrower, collateral, amount, cygnusShuttle);
    }

    //  LEVERAGE ─────────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function leverage(
        address lpTokenPair,
        address collateral,
        address borrowable,
        uint256 usdAmount,
        uint256 lpAmountMin,
        uint256 deadline,
        bytes calldata permitData,
        DexAggregator dexAggregator,
        bytes[] calldata swapData
    ) external virtual override checkDeadline(deadline) {
        // Check permit
        _checkPermit(borrowable, usdAmount, deadline, permitData);

        // Encode data to bytes
        bytes memory cygnusShuttle = _createLeverageShuttle(lpTokenPair, collateral, borrowable, lpAmountMin, dexAggregator, swapData);

        // Call borrow with encoded data
        ICygnusBorrow(borrowable).borrow(msg.sender, address(this), usdAmount, cygnusShuttle);
    }

    //  DELEVERAGE ───────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function deleverage(
        address lpTokenPair,
        address collateral,
        address borrowable,
        uint256 cygLPAmount,
        uint256 usdAmountMin,
        uint256 deadline,
        bytes calldata permitData,
        DexAggregator dexAggregator,
        bytes[] calldata swapData
    ) external virtual override checkDeadline(deadline) {
        // Permit if any
        _checkPermit(collateral, cygLPAmount, deadline, permitData);

        // Current CygLP exchange rate
        uint256 exchangeRate = ICygnusCollateral(collateral).exchangeRate();

        // Get redeem amount
        uint256 redeemAmount = cygLPAmount.mulWad(exchangeRate);

        // Encode redeem data
        bytes memory redeemData = abi.encode(
            AltairDeleverageCalldata({
                lpTokenPair: lpTokenPair,
                collateral: collateral,
                borrowable: borrowable,
                recipient: msg.sender,
                redeemTokens: cygLPAmount,
                usdAmountMin: usdAmountMin,
                dexAggregator: dexAggregator,
                swapData: swapData
            })
        );

        // Flash redeem LP Tokens
        ICygnusCollateral(collateral).flashRedeemAltair(lpTokenPair, redeemAmount, redeemData);
    }
}

contracts/cygnus-periphery/CygnusAltairX.content:
// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

// Dependencies
import {CygnusAltair} from "./CygnusAltair.sol";
import {ICygnusAltair} from "./interfaces/ICygnusAltair.sol";

// Interfaces
import {IHangar18} from "./interfaces/core/IHangar18.sol";
import {ICygnusCollateral} from "./interfaces/core/ICygnusCollateral.sol";
import {IAggregationRouterV5, IAggregationExecutor} from "./interfaces/core/IAggregationRouterV5.sol";
import {IWrappedNative} from "./interfaces/IWrappedNative.sol";

// Libraries
import {CygnusDexLib} from "./libraries/CygnusDexLib.sol";
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {FixedPointMathLib} from "./libraries/FixedPointMathLib.sol";

// Orbiter
import {IDenebOrbiter} from "./interfaces/core/IDenebOrbiter.sol";
import {IAlbireoOrbiter} from "./interfaces/core/IAlbireoOrbiter.sol";

// Strategies
import {IDexPair} from "./interfaces/core/CollateralVoid/IDexPair.sol";
import {IDexRouter} from "./interfaces/core/CollateralVoid/IDexRouter.sol";
import {ICygnusBorrow} from "./interfaces/core/ICygnusBorrow.sol";

/**
 *  @notice Router that is used to leverage, deleverage and flash liquidate. Where as CygnusAltair.sol contains
 *          the functions that are the same for all lending pools, CygnusAltairX is dex-specific.
 *  @title  CygnusAltairX Contract that handles DEX specific functionalities and core contract callbacks
 *  @author CygnusDAO
 */
contract CygnusAltairX is CygnusAltair {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          1. LIBRARIES
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib For safe transfers of Erc20 tokens
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @notice Borrowable deployer
     */
    IAlbireoOrbiter public immutable albireo;

    /**
     *  @notice Collateral deployer
     */
    IDenebOrbiter public immutable deneb;

    /**
     *  @notice Orbiter ID
     */
    uint256 public immutable orbiterId;

    /**
     *  @notice Router for the Dex swap
     */
    IDexRouter public constant DEX_ROUTER = IDexRouter(0x9c12939390052919aF3155f41Bf4160Fd3666A6f);

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Constructs the periphery contract. Factory must be deployed on the chain first to get the addresses
     *          of deployers and the wrapped native token (WETH, WFTM, etc.)
     *  @param _hangar18 The address of the Cygnus Factory contract on this chain
     */
    constructor(IHangar18 _hangar18, uint256 _orbiterId) CygnusAltair(_hangar18) {
        // Get orbiter status and names
        bool status;
        string memory orbiterName;

        // Get orbiter name
        (status, , albireo, deneb, , , , , orbiterName) = _hangar18.getOrbiters(_orbiterId);

        /// @custom:error OrbiterNotActive
        if (!status) revert CygnusAltairY__OrbitersNotActive();

        // Name
        name = string(abi.encodePacked("Cygnus-Altair-X: ", orbiterName));

        // Orbiter ID
        orbiterId = _orbiterId;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusAltair
     */
    function getAssetsForShares(
        address underlying,
        uint256 shares
    ) external view override(ICygnusAltair) returns (address[] memory tokens, uint256[] memory amounts) {
        // Get total supply of the underlying pool
        uint256 totalSupply = IDexPair(underlying).totalSupply();

        // Get reserves from the LP
        (uint256 reserves0, uint256 reserves1, ) = IDexPair(underlying).getReserves();

        // Initialize the arrays
        tokens = new address[](2);

        // Empty amounts
        amounts = new uint256[](2);

        // Get tokens
        (, , , , , address token0, address token1) = IDexPair(underlying).metadata();

        // Token 0 from the underlying
        tokens[0] = token0;

        // Token1 from the underlying
        tokens[1] = token1;

        // Same calculation as other vault tokens, asset = shares * balance / supply

        // Amount out token0 from the LP
        amounts[0] = shares.fullMulDiv(reserves0, totalSupply);

        // Amount of token1 from the LP
        amounts[1] = shares.fullMulDiv(reserves1, totalSupply);
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
          6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @param borrowable Address of the Cygnus borrow contract
     *  @param token Address of the token we are repaying (USD)
     *  @param borrower Address of the borrower who is repaying the loan
     *  @param amountMax The max available amount
     */
    function repayAndRefundPrivate(address borrowable, address token, address borrower, uint256 amountMax) internal {
        // Repay
        uint256 amount = _maxRepayAmount(borrowable, amountMax, borrower);

        // Safe transfer USD to borrowable
        token.safeTransfer(borrowable, amount);

        // Cygnus Borrow with address(0) to update borrow balances
        ICygnusBorrow(borrowable).borrow(borrower, address(0), 0, LOCAL_BYTES);

        // Refund excess
        if (amountMax > amount) {
            uint256 refundAmount = amountMax - amount;
            // Check if token is native
            if (token == nativeToken) {
                // Withdraw native
                IWrappedNative(nativeToken).withdraw(refundAmount);

                // Transfer native
                borrower.safeTransferETH(refundAmount);
            } else {
                // Transfer Token
                token.safeTransfer(borrower, refundAmount);
            }
        }
    }

    /**
     *  @notice This function gets called after calling `borrow` on Borrow contract and having `amountUsd` of USD
     *  @notice Maximum 2 swaps
     *  @param token0 The address of token0 from the LP Token
     *  @param token1 The address of token1 from the LP Token
     *  @param amountUsd The amount of USD to convert to LP
     *  @param swapData Bytes array consisting of 1inch API swap data
     *  @param reserves0 The reserves of token0
     *  @param reserves1 The reserves of token1
     *  @return liquidity The amount of LP minted
     */
    function convertUsdToLiquidity(
        DexAggregator dexAggregator,
        address token0,
        address token1,
        uint256 amountUsd,
        bytes[] memory swapData,
        uint256 reserves0,
        uint256 reserves1
    ) private returns (uint256 liquidity) {
        // Placeholder tokenA
        address tokenA;
        address tokenB;

        // ─────────────────────── 1. Check if token0 or token1 is already USD
        // Check if usd
        if (token0 == usd || token1 == usd) {
            // Assign USD to tokenA
            (tokenA, tokenB) = token0 == usd ? (token0, token1) : (token1, token0);
        } else {
            // ─────────────────── 2. If native is either token, then tokenA is native, else default
            // Check if native token
            if (token0 == nativeToken || token1 == nativeToken) {
                // If token0 is nativeToken, then assign tokenA to token0, else tokenA to token1
                (tokenA, tokenB) = token0 == nativeToken ? (token0, token1) : (token1, token0);
            } else {
                // Assign tokenA to token0 and tokenB to token1 as default
                (tokenA, tokenB) = (token0, token1);
            }

            // Swap USD to tokenA using dex aggregator
            _swapTokensAggregator(dexAggregator, swapData[0], usd, amountUsd);
        }

        // ─────────────────────── 3. Calculate optimal deposit amount for an LP Token
        // Amount A
        uint256 totalAmountA = _checkBalance(tokenA);

        // Get reserves A for calculating optimal deposit
        uint256 reservesA = tokenA == token0 ? reserves0 : reserves1;

        // Get optimal swap amount for token A (use maximum fee possible for VELO = 0.05%)
        uint256 swapAmount = CygnusDexLib.optimalDepositA(totalAmountA, reservesA, 5);

        // ─────────────────────── 4. Swap `optimalTokenA` amount to tokenB directly on the pair
        // Swap tokens
        uint256 totalAmountB = swapTokensDex(tokenA, tokenB, swapAmount);

        // LP Token
        liquidity = addLiquidityPrivate(tokenA, tokenB, totalAmountA - swapAmount, totalAmountB);
    }

    /**
     *  @notice Converts an amount of LP Token to USD. It is called after calling `burn` on a uniswapV2 pair, which
     *          receives amountTokenA of token0 and amountTokenB of token1.
     *  @notice Maximum 2 swaps
     *  @param amountTokenA The amount of token A to convert to USD
     *  @param amountTokenB The amount of token B to convert to USD
     *  @param token0 The address of token0 from the LP Token pair
     *  @param token1 The addre.s of token1 from the LP Token pair
     *  @param swapData Bytes array consisting of 1inch API swap data
     */
    function convertLiquidityToUsd(
        DexAggregator dexAggregator,
        uint256 amountTokenA,
        uint256 amountTokenB,
        address token0,
        address token1,
        bytes[] memory swapData
    ) private returns (uint256) {
        // ─────────────────────── 1. Check if token0 or token1 is already USD
        uint256 amountA;
        uint256 amountB;

        // If token0 or token1 is USD then swap opposite
        if (token0 == usd || token1 == usd) {
            // Convert the other token to USD and return
            (amountA, amountB) = token0 == usd
                ? (amountTokenA, _swapTokensAggregator(dexAggregator, swapData[1], token1, amountTokenB))
                : (_swapTokensAggregator(dexAggregator, swapData[0], token0, amountTokenA), amountTokenB);

            // Explicit return
            return amountA + amountB;
        }

        // ─────────────────── 2. Not USD, swap both to USD
        // Swap token0 to USD

        amountA = _swapTokensAggregator(dexAggregator, swapData[0], token0, amountTokenA);

        // Swap token1 to USD
        amountB = _swapTokensAggregator(dexAggregator, swapData[1], token1, amountTokenB);

        // USD balance
        return amountA + amountB;
    }

    // 2 FUNCTIONS: - SWAP TOKENS ON THE DEX
    //              - ADD LIQUIDITY TO THE DEX

    /**
     *  @notice Swap tokens using the dex router for the second swap
     *  @param tokenIn The address of the token we are swapping
     *  @param tokenOut The address of the token we are receiving
     *  @param amount Amount of `tokenIn` we are swapping to `tokenOut`
     *  @return Amount received of `tokenOut`
     */
    function swapTokensDex(address tokenIn, address tokenOut, uint256 amount) private returns (uint256) {
        // Check approve tokenIn
        _approveToken(tokenIn, address(DEX_ROUTER), amount);

        // Swap tokenIn to tokenOut
        uint256[] memory amounts = DEX_ROUTER.swapExactTokensForTokensSimple(
            amount,
            0,
            tokenIn,
            tokenOut,
            false,
            address(this),
            block.timestamp
        );

        // Return amount out of tokenOut (last index)
        return amounts[amounts.length - 1];
    }

    /**
     *  @notice Function to add liquidity and mint LP Tokens
     *  @param tokenA Address of the LP Token's token0
     *  @param tokenB Address of the LP Token's token1
     *  @param amountA Amount of token A to add as liquidity
     *  @param amountB Amount of token B to add as liquidity
     *  @return liquidity The total LP Tokens minted
     */
    function addLiquidityPrivate(address tokenA, address tokenB, uint256 amountA, uint256 amountB) private returns (uint256 liquidity) {
        // Check tokenA approve
        _approveToken(tokenA, address(DEX_ROUTER), amountA);

        // Check tokenB approve
        _approveToken(tokenB, address(DEX_ROUTER), amountB);

        // Mint LP
        (, , liquidity) = DEX_ROUTER.addLiquidity(tokenA, tokenB, false, amountA, amountB, 0, 0, address(this), block.timestamp);
    }

    /**
     *  @notice Liquidates borrower internally and converts LP Tokens to receive back USD
     *  @param lpTokenPair The address of the LP Token
     *  @param collateral The address of the Cygnus Collateral
     *  @param borrowable The address of the borrowable contract
     *  @param seizeTokens The amount of CygLP seized
     *  @param swapData The 1inch calldata to swap LP back to USD
     *  @return usdAmount The amount of USD received
     */
    function flashLiquidatePrivate(
        address lpTokenPair,
        address collateral,
        address borrowable,
        address recipient,
        uint256 seizeTokens,
        uint256 repayAmount,
        DexAggregator dexAggregator,
        bytes[] memory swapData
    ) private returns (uint256 usdAmount) {
        // Calculate LP amount to redeem
        uint256 redeemAmount = seizeTokens.mulWad(ICygnusCollateral(collateral).exchangeRate());

        // Flash redeem the LP back to the contract
        ICygnusCollateral(collateral).flashRedeemAltair(lpTokenPair, redeemAmount, LOCAL_BYTES);

        // Burn LP Token and return amountA and amountB
        (uint256 amountAMax, uint256 amountBMax) = IDexPair(lpTokenPair).burn(address(this));

        // Token0 and token1 from the LP
        (, , , , , address token0, address token1) = IDexPair(lpTokenPair).metadata();

        // Convert amountA and amountB to USD
        convertLiquidityToUsd(dexAggregator, amountAMax, amountBMax, token0, token1, swapData);

        // Manually get the balance, this is because in some cases the amount returned by aggregators is not 100% accurate
        usdAmount = _checkBalance(usd);

        /// @custom:error InsufficientLiquidateUsd Avoid if received is less than liquidated
        if (usdAmount < repayAmount) revert CygnusAltair__InsufficientLiquidateUsd();

        // Transfer USD to recipient
        usd.safeTransfer(recipient, usdAmount - repayAmount);

        // Transfer the repay amount of USD to borrowable
        usd.safeTransfer(borrowable, repayAmount);
    }

    /**
     *  @notice Mints liquidity from the DEX and deposits in the collateral, minting CygLP to the recipient
     *  @param lpTokenPair The address of the LP Token
     *  @param recipient The address of the recipient
     *  @param collateral The address of the Cygnus Collateral contract
     *  @param borrowAmount The amount of USD borrowed to conver to LP
     *  @param lpAmountMin The minimum amount of LP allowed to receive
     *  @param swapdata Swap data for aggregator
     */
    function mintLPAndDepositPrivate(
        address lpTokenPair,
        address recipient,
        address collateral,
        uint256 borrowAmount,
        uint256 lpAmountMin,
        DexAggregator dexAggregator,
        bytes[] memory swapdata
    ) private {
        // Get token metadata
        (, , uint256 reserves0, uint256 reserves1, , address token0, address token1) = IDexPair(lpTokenPair).metadata();

        // Converts the borrowed amount of USD to tokenA and tokenB to mint the LP Token
        uint256 liquidity = convertUsdToLiquidity(dexAggregator, token0, token1, borrowAmount, swapdata, reserves0, reserves1);

        /// @custom:error InsufficientLPTokenAmount Avoid if LP Token amount received is less than min
        if (liquidity < lpAmountMin) revert CygnusAltair__InsufficientLPTokenAmount();

        // Check allowance and deposit the LP token in the collateral contract
        _approveToken(lpTokenPair, address(PERMIT2), liquidity);

        // Approve Permit
        _approvePermit2(lpTokenPair, collateral, liquidity);

        // Mint CygLP to the recipient
        ICygnusCollateral(collateral).deposit(liquidity, recipient, emptyPermit, LOCAL_BYTES);

        // Check for dust from after leverage
        cleanDustPrivate(token0, token1, recipient);
    }

    /**
     *  @notice Removes liquidity from the Dex by calling the pair's `burn` function, receiving tokenA and tokenB
     *  @notice Ideally the dex would already have the LP so we can just call burn (check `deleveragePrivate()`)
     *  @param lpTokenPair The address of the LP Token
     *  @param borrower The address of the recipient
     *  @param collateral The address of the Cygnus Collateral contract
     *  @param borrowable The address of the Cygnus Borrow contract
     *  @param redeemTokens The amount of CygLP to redeem
     *  @param usdAmountMin The minimum amount of USD allowed to receive
     *  @param swapdata Swap data for aggregator
     */
    function removeLPAndRepayPrivate(
        address lpTokenPair,
        address borrower,
        address collateral,
        address borrowable,
        uint256 redeemTokens,
        uint256 usdAmountMin,
        DexAggregator dexAggregator,
        bytes[] memory swapdata
    ) private {
        // Burn and return amountA and amountB
        (uint256 amountAMax, uint256 amountBMax) = IDexPair(lpTokenPair).burn(address(this));

        // Get token0 and token1
        (, , , , , address token0, address token1) = IDexPair(lpTokenPair).metadata();

        // Convert tokens to USD using Paraswap aggregator
        convertLiquidityToUsd(dexAggregator, amountAMax, amountBMax, token0, token1, swapdata);

        // Manually get the balance, this is because in some cases the amount returned by aggregators is not 100% accurate
        uint256 usdAmount = _checkBalance(usd);

        /// @custom:error InsufficientRedeemAmount Avoid if USD received is less than min
        if (usdAmount < usdAmountMin) revert CygnusAltair__InsufficientUSDAmount();

        // Repay USD
        repayAndRefundPrivate(borrowable, usd, borrower, usdAmount);

        // repay flash redeem
        ICygnusCollateral(collateral).transferFrom(borrower, collateral, redeemTokens);
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    //  LIQUIDATE ─────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function altairLiquidate_f2x(
        address sender,
        uint256 cygLPAmount,
        uint256 repayAmount,
        bytes calldata data
    ) external virtual override(ICygnusAltair) {
        // Decode data passed from borrow contract
        AltairLiquidateCalldata memory cygnusShuttle = abi.decode(data, (AltairLiquidateCalldata));

        /// @custom:error MsgSenderNotRouter Avoid if the caller is not the router
        if (sender != address(this)) {
            // solhint-disable-next-line
            revert CygnusAltair__MsgSenderNotRouter({sender: sender, origin: tx.origin});
        }
        /// @custom:error MsgSenderNotBorrowable Avoid if the msg sender is not the borrow contract
        else if (msg.sender != cygnusShuttle.borrowable) {
            revert CygnusAltair__MsgSenderNotBorrowable({sender: msg.sender, borrowable: cygnusShuttle.borrowable});
        }

        // Convert CygLP to USD
        flashLiquidatePrivate(
            cygnusShuttle.lpTokenPair,
            cygnusShuttle.collateral,
            cygnusShuttle.borrowable,
            cygnusShuttle.recipient,
            cygLPAmount, // Seized amount of CygLP
            repayAmount,
            cygnusShuttle.dexAggregator,
            cygnusShuttle.swapData
        );
    }

    //  LEVERAGE ─────────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function altairBorrow_O9E(address sender, uint256 borrowAmount, bytes calldata data) external virtual override(ICygnusAltair) {
        // Decode data passed from borrow contract
        AltairLeverageCalldata memory cygnusShuttle = abi.decode(data, (AltairLeverageCalldata));

        /// @custom:error MsgSenderNotRouter Avoid if the caller is not the router
        if (sender != address(this)) {
            // solhint-disable-next-line
            revert CygnusAltair__MsgSenderNotRouter({sender: sender, origin: tx.origin});
        }
        /// @custom:error MsgSenderNotBorrowable Avoid if the msg sender is not the borrow contract
        else if (msg.sender != cygnusShuttle.borrowable) {
            revert CygnusAltair__MsgSenderNotBorrowable({sender: msg.sender, borrowable: cygnusShuttle.borrowable});
        }

        // Mint LP and deposit in collateral
        mintLPAndDepositPrivate(
            cygnusShuttle.lpTokenPair,
            cygnusShuttle.recipient,
            cygnusShuttle.collateral,
            borrowAmount,
            cygnusShuttle.lpAmountMin,
            cygnusShuttle.dexAggregator,
            cygnusShuttle.swapData
        );
    }

    //  DELEVERAGE ───────────────────────────────────

    /**
     *  @inheritdoc ICygnusAltair
     */
    function altairRedeem_u91A(address sender, uint256 redeemAmount, bytes calldata data) external virtual override(ICygnusAltair) {
        // Decode deleverage shuttle data
        AltairDeleverageCalldata memory redeemData = abi.decode(data, (AltairDeleverageCalldata));

        /// @custom:error MsgSenderNotRouter Avoid if the caller is not the router
        if (sender != address(this)) {
            // solhint-disable-next-line
            revert CygnusAltair__MsgSenderNotRouter({sender: sender, origin: tx.origin});
        }
        /// @custom:error MsgSenderNotCollateral Avoid if the msg sender is not Cygnus collateral contract
        else if (msg.sender != redeemData.collateral) {
            revert CygnusAltair__MsgSenderNotCollateral({sender: msg.sender, collateral: redeemData.collateral});
        }

        // Shh
        redeemAmount;

        // Burn LP and withdraw from collateral
        removeLPAndRepayPrivate(
            redeemData.lpTokenPair,
            redeemData.recipient,
            redeemData.collateral,
            redeemData.borrowable,
            redeemData.redeemTokens,
            redeemData.usdAmountMin,
            redeemData.dexAggregator,
            redeemData.swapData
        );
    }

    /**
     *  @notice Send dust to user who leveraged USDC into LP, if any
     *  @param token0 The address of token0 from the LP
     *  @param token1 The address of token1 from the LP
     *  @param recipient The address of the user leveraging the position
     */
    function cleanDustPrivate(address token0, address token1, address recipient) private {
        // Check for dust of token0
        uint256 leftAmount0 = _checkBalance(token0);

        // Check for dust of token1
        uint256 leftAmount1 = _checkBalance(token1);

        // Send leftover token0 to user
        if (leftAmount0 > 0) token0.safeTransfer(recipient, leftAmount0);

        // Send leftover token1 to user
        if (leftAmount1 > 0) token1.safeTransfer(recipient, leftAmount1);
    }
}

